import json

# Get input data from n8n
input_data = items[0]['json']

# Handle if data is a list with single dict
if isinstance(input_data, list) and len(input_data) > 0:
    data = input_data[0]
else:
    data = input_data

# Start HTML
html = """<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Crypto Market Analysis</title>
    <style>
        * {
            margin: 0;
            padding: 0;
            box-sizing: border-box;
        }

        body {
            font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
            background: #000000;
            color: #00ff41;
            line-height: 1.6;
            padding: 20px;
        }

        .container {
            max-width: 1200px;
            margin: 0 auto;
        }

        .post-card {
            background: #0a0a0a;
            border-radius: 15px;
            padding: 30px;
            margin-bottom: 30px;
            border: 1px solid #00ff41;
            box-shadow: 0 0 20px rgba(0, 255, 65, 0.3);
            transition: transform 0.3s ease, box-shadow 0.3s ease;
        }

        .post-card:hover {
            transform: translateY(-5px);
            box-shadow: 0 0 30px rgba(0, 255, 65, 0.5);
        }

        .post-number {
            color: #00ff41;
            font-size: 1.2em;
            font-weight: bold;
            margin-bottom: 15px;
            display: inline-block;
            padding: 5px 15px;
            background: rgba(0, 255, 65, 0.1);
            border-radius: 20px;
            border: 1px solid #00ff41;
            cursor: pointer;
            transition: all 0.3s ease;
        }

        .post-number:hover {
            background: rgba(0, 255, 65, 0.2);
            box-shadow: 0 0 15px rgba(0, 255, 65, 0.5);
        }

        .post-number:active {
            transform: scale(0.95);
        }

        .copy-notification {
            position: fixed;
            top: 20px;
            right: 20px;
            background: #00ff41;
            color: #000000;
            padding: 15px 25px;
            border-radius: 10px;
            font-weight: bold;
            box-shadow: 0 0 20px rgba(0, 255, 65, 0.6);
            z-index: 1000;
            animation: slideIn 0.3s ease;
        }

        @keyframes slideIn {
            from {
                transform: translateX(400px);
                opacity: 0;
            }
            to {
                transform: translateX(0);
                opacity: 1;
            }
        }

        .post-content {
            font-size: 1.05em;
            margin-bottom: 25px;
            text-align: justify;
            color: #ffffff;
        }

        .image-prompt-section {
            background: #050505;
            border-radius: 10px;
            padding: 20px;
            border-left: 4px solid #00d4ff;
        }

        .image-prompt-label {
            color: #00d4ff;
            font-weight: bold;
            font-size: 1.1em;
            margin-bottom: 10px;
            display: flex;
            align-items: center;
        }

        .image-prompt-label::before {
            content: "ðŸŽ¨";
            margin-right: 10px;
            font-size: 1.2em;
        }

        .image-prompt-content {
            color: #aaaaaa;
            font-style: italic;
            line-height: 1.8;
        }

        @media (max-width: 768px) {
            body {
                padding: 10px;
            }

            h1 {
                font-size: 2em;
            }

            .post-card {
                padding: 20px;
            }
        }
    </style>
</head>
<body>
    <div class="container">
"""

# Extract posts dynamically
post_count = 1
while f"post{post_count}" in data:
    post_key = f"post{post_count}"
    img_key = f"imgpromt{post_count}"

    # Escape single quotes in post content for JavaScript
    post_content = data[post_key].replace("'", "\\'").replace("\n", " ")

    html += f"""
        <div class="post-card">
            <div class="post-number" onclick="copyPost('{post_content}', {post_count})">Post #{post_count}</div>
            <div class="post-content">
                {data[post_key]}
            </div>
"""

    # Add image prompt if exists
    if img_key in data:
        html += f"""
            <div class="image-prompt-section">
                <div class="image-prompt-label">Image Prompt</div>
                <div class="image-prompt-content">
                    {data[img_key]}
                </div>
            </div>
"""

    html += """
        </div>
"""
    post_count += 1

# Close HTML
html += """
    </div>

    <script>
        function copyPost(content, postNum) {
            // Create temporary textarea
            const textarea = document.createElement('textarea');
            textarea.value = content;
            textarea.style.position = 'fixed';
            textarea.style.opacity = '0';
            document.body.appendChild(textarea);

            // Select and copy
            textarea.select();
            textarea.setSelectionRange(0, 99999);

            try {
                document.execCommand('copy');
                showNotification('Post #' + postNum + ' copied!');
            } catch (err) {
                showNotification('Failed to copy');
            }

            document.body.removeChild(textarea);
        }

        function showNotification(message) {
            // Remove existing notification
            const existing = document.querySelector('.copy-notification');
            if (existing) {
                existing.remove();
            }

            // Create notification
            const notification = document.createElement('div');
            notification.className = 'copy-notification';
            notification.textContent = message;
            document.body.appendChild(notification);

            // Remove after 2 seconds
            setTimeout(() => {
                notification.style.animation = 'slideIn 0.3s ease reverse';
                setTimeout(() => notification.remove(), 300);
            }, 2000);
        }
    </script>
</body>
</html>"""

# Return in n8n format
return [{'json': {'html': html}}]

