post_count = 1
while f"post{post_count}" in data:
    post_key = f"post{post_count}"
    img_key = f"imgpromt{post_count}"

    # Escape single quotes in post content for JavaScript
    post_content = data[post_key].replace("'", "\\'").replace("\n", " ")

    html += f"""
        <div class="post-card">
            <div class="post-number" onclick="copyPost('{post_content}', {post_count})">Post #{post_count}</div>
            <div class="post-content">
                {data[post_key]}
            </div>
"""

    # Add image prompt if exists
    if img_key in data:
        html += f"""
            <div class="image-prompt-section">
                <div class="image-prompt-label">Image Prompt</div>
                <div class="image-prompt-content">
                    {data[img_key]}
                </div>
            </div>
"""

    html += """
        </div>
"""
    post_count += 1

# Close HTML
html += """
    </div>

    <script>
        function copyPost(content, postNum) {
            // Create temporary textarea
            const textarea = document.createElement('textarea');
            textarea.value = content;
            textarea.style.position = 'fixed';
            textarea.style.opacity = '0';
            document.body.appendChild(textarea);

            // Select and copy
            textarea.select();
            textarea.setSelectionRange(0, 99999);

            try {
                document.execCommand('copy');
                showNotification('Post #' + postNum + ' copied!');
            } catch (err) {
                showNotification('Failed to copy');
            }

            document.body.removeChild(textarea);
        }

        function showNotification(message) {
            // Remove existing notification
            const existing = document.querySelector('.copy-notification');
            if (existing) {
                existing.remove();
            }

            // Create notification
            const notification = document.createElement('div');
            notification.className = 'copy-notification';
            notification.textContent = message;
            document.body.appendChild(notification);

            // Remove after 2 seconds
            setTimeout(() => {
                notification.style.animation = 'slideIn 0.3s ease reverse';
                setTimeout(() => notification.remove(), 300);
            }, 2000);
        }
    </script>
</body>
</html>"""

# Return in n8n format
return [{'json': {'html': html}}]

